update clinlims.test set description=replace(description, ' Total', '') where description like '%Sang Total%';
update clinlims.sampletype_test set sample_type_id=(select id from clinlims.type_of_sample where description='Sang') where sample_type_id=(select id from clinlims.type_of_sample where description='Sang Total');
update clinlims.sampletype_panel set sample_type_id=(select id from clinlims.type_of_sample where description='Sang') where sample_type_id=(select id from clinlims.type_of_sample where description='Sang Total');
update clinlims.sample_item set typeosamp_id=(select id from clinlims.type_of_sample where description='Sang') where typeosamp_id=(select id from clinlims.type_of_sample where description='Sang Total');
update clinlims.type_of_sample set is_active='N' where description='Sang Total';